mapper.select:
SELECT ID, PASSWORD, MANAGERNAME, PHONENUM, ADTEL, ADNAME, ADDRESS, TOTALHC, CURRHC, INTRODUCTION, PROFILEIMAGE, TOKEN
FROM Member
WHERE ID=#{id} AND PASSWORD=#{password}

mapper.update:
UPDATE Member SET PASSWORD=#{password},
                  INTRODUCTION=#{introduction}, ADTEL=#{adTel}, ADNAME=#{adName}, ADDRESS=#{address}, TOTALHC=#{totalHc}, PROFILEIMAGE=#{profileImage}
WHERE ID=#{id}

mapper.select:
SELECT ID, PASSWORD
FROM Member
where ID = #{id} AND PASSWORD = #{password}

mapper.update:
UPDATE Member
SET EXPOTOKEN=#{expotoken} AND FCMTOKEN=#{fcmtoken}
WHERE ID = #{id}
