mapper.update:
UPDATE ReviewThx SET reviewTitle=#{reviewTitle},
                     reviewContext=#{reviewContext}, reviewDate=NOW()
WHERE receiver = #{receiver}

UPDATE ReviewThx
SET REVIEWIMAGE = #{reviewImage}
WHERE receiver = #{receiver}

mapper.select:
SELECT regionCategory, reviewTitle, reviewContext, donator, receiver, reviewDate, reviewImage
FROM   ReviewThx
where  donator=#{donator}

SELECT regionCategory, reviewTitle, reviewContext, donator, receiver, reviewDate, reviewImage
FROM   ReviewThx
where  receiver=#{receiver}
